import React, { useState }  from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronDown, faChevronUp, faCircle, faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';
import './App.css';
import axios from 'axios';

function App() {
  const [data, setData] = useState({})
  const [location, setLocation] = useState('')

  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&units=imperial&appid=a86ac6f164e80a61d42fa9ea208dbe2b`;

  const searchLocation = (event) => {
    if(event.key === 'Enter') {
      axios.get(url).then((response) => {
        setData(response.data)
        console.log(response.data)
      })
      setLocation('')
    }
  }

  const currentTime = this.data.dt;

  return (
    <div>
      <header>
        <div className='search'>
          <input value={location} onChange={event => setLocation(event.target.value)} onKeyUp={searchLocation} placeholder="Enter Location" type="text" />
          <FontAwesomeIcon icon={faMagnifyingGlass} className='icon' />
        </div>
        <div className='location'>{data.name}</div>
        <div className='units'>C°/F°</div>
      </header>
      <div className='main'>
        <div className='icon'>
          <FontAwesomeIcon icon={faCircle} className='image' />
        </div>
        <div className='temp'>
          <div className='low-temp'>
            <FontAwesomeIcon icon={faChevronDown} />
            {data.main ? <h2>{data.main.temp_min.toFixed()}°</h2> : null} 
          </div>
          {data.main ? <h1>{data.main.temp.toFixed()}°</h1> : null} 
          <div className='high-temp'>
            <FontAwesomeIcon icon={faChevronUp} />
            {data.main ? <h2>{data.main.temp_max.toFixed()}°</h2> : null} 
          </div>
        </div>
        <div className='sun'>
          <div className='sunrise'>
            <p>Sunrise</p>
            {/*data.sys ? <p>{Date((data.dt + data.timezone))}</p> : null*/} 
            {currentTime} 
          </div>
          <div className='sunset'>
            <p>Sunset</p>
            {/*data.sys ? <p>{Date((data.sys.sunset + data.timezone) * 1000)}</p> : null*/} 
          </div>

        </div>
      </div>
    </div>
  );
}

export default App;
