import React, { useState, useEffect }  from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronDown, faChevronUp, faCircle, faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';
import moment from 'moment/moment';
import axios from 'axios';

const Weather = () => {
    const [search, setSearch] = useState("banning");
    const [data, setData] = useState([]);
    const [location, setLocation] = useState('');
    
    if (location == null) {
      location = search;
    }

    const fetchWeather = async () => {
      const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${location}&units=imperial&appid=a86ac6f164e80a61d42fa9ea208dbe2b`);
      
      response.json().then(response => 
      {      
        setData(response);
      })
    }
    
    const searchLocation = (e) => {
      if(e.key === 'Enter') {

      }
    }

    useEffect(() => {
      fetchWeather();
    }, []);
    
    const timezone = data?.timezone; //needs to be converted in minutes 
    const timezoneInMinutes = timezone / 60;
    const currTime = moment().utcOffset(timezoneInMinutes).format("h:mm A");

    // Sunrise
    const sunriseTime = data?.sys?.sunrise;
    const sunrise = moment.unix(sunriseTime).utc().add(timezone, 's').format('LT');
    
    //Sunset
    const sunsetTime = data?.sys?.sunset;
    const sunset = moment.unix(sunsetTime).utc().add(timezone, 's').format('LT');

    return (
      <div className='body'>
        <header>
          <div className='search'>
            <input value={location} onKeyUp={fetchWeather} placeholder="Enter Location" type="text" />
            <FontAwesomeIcon icon={faMagnifyingGlass} className='icon' />
          </div>
          <div className='location'>{data.name}</div>
          <div className='units'>
            <div className='imperial'>
              F°
            </div>
            <p>/</p>
            <div className='metric'>
              C°
            </div>
          </div>
        </header>
        <div className='main'>
          <div className='icon'>
            <FontAwesomeIcon icon={faCircle} className='image' />
          </div>
          <div className='temp'>
            <div className='low-temp'>
              <FontAwesomeIcon icon={faChevronDown} />
              {data.main ? <h2>{data.main.temp_min.toFixed()}°</h2> : null} 
            </div>
            {data.main ? <h1>{data.main.temp.toFixed()}°</h1> : null} 
            <div className='high-temp'>
              <FontAwesomeIcon icon={faChevronUp} />
              {data.main ? <h2>{data.main.temp_max.toFixed()}°</h2> : null} 
            </div>
          </div>
          <div className='sun'>
            <div className='sunrise'>
              <p>Sunrise</p>
              {sunrise}  
            </div>
            <div className='sunset'>
              <p>Sunset</p>
              {sunset} 
            </div>
  
          </div>
        </div>
      </div>
    );
  }
  
  export default Weather;
